query.insert:
insert into board
(
	board_id
	,board_category_id
	,board_title
	,board_content
	,board_writer
	,board_date
)
values
(
	seq_board.nextval
	,${boardCategoryId}
	,${boardTitle}
	,${boardContent}
	,${boardWriter}
	,TO_DATE(${boardDate},'YYYY-MM-DD HH24:mi:SS')
)

query.update:
update 
	board 
set
	board_title = ${boardTitle}
	,board_content = ${boardContent}
where
	board_id = ${boardId}
	and board_category_id = ${boardCategoryId}
	and board_writer = ${boardWriter}

query.delete:
delete from board 
where
	board_id = ${boardId}
	and board_category_id = ${boardCategoryId}
	and board_writer = ${boardWriter}
